var PKStore = function PKStore() {};

PKStore.prototype.get = function get(cb) {
    var self = this;
    // var key = I.Const.GLOBAL_KEY_PREFIX + name; TODO put this to extends class
    db.get(this.key, function(err, data) {
        if (err) return cb(new I.Exception(10125, err));
        var pk = new self.model(data);
        cb(null, data);
    });
};

PKStore.prototype.set = function set(pk, cb) {
    // TODO add verify instance
    cb = cb || function() {};
    db.set(this.key, function(err, result) {
        if (err) return cb(new I.Exception(10124, err));
        cb();
    });
};

if (exports) exports.PKStore = PKStore;

